	public void testParse() {
		Gson gson = new GsonBuilder().create();
		WeekSchedule week = gson.fromJson(json, WeekSchedule.class);
		
		assertNotNull(week);
		assertEquals(2011, week.getSeason());
		assertEquals("reg", week.getType());
		
		Game game0 = week.getGames().get(0);
		assertNotNull(game0);
		
		assertTrue(game0.isInProgress());
		assertTrue(game0.isHalftime());
	}
